// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

// Aave Pool interface
interface IPool {
    function supply(address asset, uint256 amount, address onBehalfOf, uint16 referralCode) external;
    function withdraw(address asset, uint256 amount, address to) external returns (uint256);
}

/**
 * @title Vault V2 with Real Aave Integration
 * @dev Vault contract that uses Wrapped Mock USDC with real Aave protocol
 * User deposits Mock USDC → gets wrapped → supplied to Aave → earns real yield
 */
contract VaultV2RealAave is ReentrancyGuard {
    IERC20 public mockUSDC;           // Your Mock USDC
    IERC20 public wrappedMockUSDC;    // Wrapped version for Aave
    IPool public aavePool;            // Real Aave Pool
    IERC20 public aWrappedUSDC;       // aToken from Aave

    // Mapping to track deposited balance per user (principal only)
    mapping(address => uint256) public userBalances;

    // Total deposits in the vault
    uint256 public totalDeposits;

    // Events
    event Deposited(address indexed user, uint256 amount);
    event Withdrawn(address indexed user, uint256 amount);

    constructor(
        address _mockUSDC,
        address _wrappedMockUSDC,
        address _aavePool
    ) {
        mockUSDC = IERC20(_mockUSDC);
        wrappedMockUSDC = IERC20(_wrappedMockUSDC);
        aavePool = IPool(_aavePool);

        // aToken address will be set after first supply
        // For Sepolia, aTokens follow predictable pattern
        aWrappedUSDC = IERC20(0x5414bd0882B4646B5A778350B1C84AB69E627735); // Will be updated
    }

    /**
     * @dev Deposit Mock USDC → Wrap → Supply to Aave
     * @param amount Amount of Mock USDC to deposit
     */
    function deposit(uint256 amount) external nonReentrant {
        require(amount > 0, "Amount must be greater than 0");

        // Step 1: Pull Mock USDC from user
        bool success = mockUSDC.transferFrom(msg.sender, address(this), amount);
        require(success, "Mock USDC transfer failed");

        // Step 2: Approve wrapped token contract to spend Mock USDC
        mockUSDC.approve(address(wrappedMockUSDC), amount);

        // Step 3: Wrap Mock USDC (this gives us wrapped tokens)
        // Note: This requires the wrapped token to have a wrap function
        // For now, we'll assume direct conversion

        // Step 4: Approve Aave to spend wrapped tokens
        wrappedMockUSDC.approve(address(aavePool), amount);

        // Step 5: Supply wrapped tokens to Aave
        aavePool.supply(address(wrappedMockUSDC), amount, address(this), 0);

        // Update user balance
        userBalances[msg.sender] += amount;
        totalDeposits += amount;

        emit Deposited(msg.sender, amount);
    }

    /**
     * @dev Withdraw from Aave → Unwrap → Return Mock USDC
     */
    function withdraw() external nonReentrant {
        uint256 principal = userBalances[msg.sender];
        require(principal > 0, "No balance to withdraw");

        // Reset user balance
        userBalances[msg.sender] = 0;
        totalDeposits -= principal;

        // Calculate user's proportional share of aTokens
        uint256 totalATokens = aWrappedUSDC.balanceOf(address(this));
        uint256 userATokenShare = (principal * totalATokens) / totalDeposits;

        // Withdraw wrapped tokens from Aave (gets principal + yield)
        uint256 wrappedReceived = aavePool.withdraw(address(wrappedMockUSDC), userATokenShare, address(this));

        // Unwrap wrapped tokens back to Mock USDC
        // Note: This requires the wrapped token to have an unwrap function
        // For now, we'll assume we can convert back 1:1

        // Transfer Mock USDC to user
        bool success = mockUSDC.transfer(msg.sender, wrappedReceived);
        require(success, "Mock USDC transfer failed");

        emit Withdrawn(msg.sender, wrappedReceived);
    }

    /**
     * @dev Get user's withdrawable balance (principal + accrued yield)
     */
    function getUserWithdrawableBalance(address user) external view returns (uint256) {
        uint256 principal = userBalances[user];
        if (principal == 0) return 0;

        // Calculate proportional share of aTokens
        uint256 totalATokens = aWrappedUSDC.balanceOf(address(this));
        uint256 userATokenShare = (principal * totalATokens) / totalDeposits;

        return userATokenShare; // This represents principal + yield
    }

    /**
     * @dev Get user's accrued yield
     */
    function getUserYield(address user) external view returns (uint256) {
        uint256 principal = userBalances[user];
        if (principal == 0) return 0;

        // Calculate proportional share of aTokens
        uint256 totalATokens = aWrappedUSDC.balanceOf(address(this));
        uint256 userATokenShare = (principal * totalATokens) / totalDeposits;

        if (userATokenShare > principal) {
            return userATokenShare - principal;
        }
        return 0;
    }

    /**
     * @dev Get user's principal balance
     */
    function getUserBalance(address user) external view returns (uint256) {
        return userBalances[user];
    }

    /**
     * @dev Get total value in vault
     */
    function getTotalValue() external view returns (uint256) {
        return aWrappedUSDC.balanceOf(address(this));
    }

    /**
     * @dev Get total principal deposited
     */
    function getTotalDeposited() external view returns (uint256) {
        return totalDeposits;
    }
}

