// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title Wrapped Mock USDC
 * @dev ERC20 wrapper for Mock USDC that can be whitelisted in Aave
 * 1:1 backed by Mock USDC, compatible with Aave protocol
 */
contract WrappedMockUSDC is ERC20, Ownable {
    IERC20 public mockUSDC;

    // Events
    event Wrapped(address indexed user, uint256 amount);
    event Unwrapped(address indexed user, uint256 amount);

    constructor(address _mockUSDCAddress) ERC20("Wrapped Mock USDC", "wmUSDC") Ownable(msg.sender) {
        mockUSDC = IERC20(_mockUSDCAddress);
    }

    /**
     * @dev Wrap Mock USDC into Wrapped Mock USDC (1:1)
     * @param amount Amount of Mock USDC to wrap
     */
    function wrap(uint256 amount) external {
        require(amount > 0, "Amount must be greater than 0");

        // Pull Mock USDC from user
        bool success = mockUSDC.transferFrom(msg.sender, address(this), amount);
        require(success, "Mock USDC transfer failed");

        // Mint Wrapped Mock USDC to user (1:1)
        _mint(msg.sender, amount);

        emit Wrapped(msg.sender, amount);
    }

    /**
     * @dev Unwrap Wrapped Mock USDC back to Mock USDC (1:1)
     * @param amount Amount of Wrapped Mock USDC to unwrap
     */
    function unwrap(uint256 amount) external {
        require(amount > 0, "Amount must be greater than 0");

        // Burn Wrapped Mock USDC from user
        _burn(msg.sender, amount);

        // Transfer Mock USDC back to user (1:1)
        bool success = mockUSDC.transfer(msg.sender, amount);
        require(success, "Mock USDC transfer failed");

        emit Unwrapped(msg.sender, amount);
    }

    /**
     * @dev Mint Wrapped Mock USDC (only owner, for funding)
     * @param to Address to mint to
     * @param amount Amount to mint
     */
    function mint(address to, uint256 amount) external onlyOwner {
        _mint(to, amount);
    }

    /**
     * @dev Get underlying Mock USDC address
     */
    function getUnderlying() external view returns (address) {
        return address(mockUSDC);
    }
}
